    });

    //********************Registration Fancybox

    jQuery("a.emailBtn").on('click', function(e) {

        e.preventDefault();
        e.stopPropagation();
        //alert('a.openform');
        jQuery('#js-cogrEmessage').removeClass('js-cogrEmessageHidden')

        console.log('openform click');

        jQuery.fancybox({
            margin: 0,
            padding: 0,
            width: 300,
            height: 100,
            closeBtn : false, 
            autoScale: false,
            autoDimensions: true,
            showCloseButton: true,
            modal: false,
            href: '#myEmailForm'
        });

        //	hideOnOverlayClick : true

    });
});

